import React, { Component } from "react";
import { InputGroup, Form, Button, Row, Col, Container } from "react-bootstrap";
import 'font-awesome/css/font-awesome.min.css';
import axios from 'axios';
import "../login.css";

let loginFooter = 'Login-trees.png'

const footerStyle = {
  backgroundImage: 'url(' + loginFooter + ')',
};

class Login extends Component {
  constructor(props){
    super(props);
    this.state={
      username:'',
      password:''
    }
  }

  Footer = ({ children }) => {
    return (
      <div>
        <div className="login-footer" style={footerStyle} >{children}</div>
      </div>
    );
  }

  postLogin = (e) => {
    e.preventDefault();

    const email = e.target.elements.formBasicEmail.value;
    const password = e.target.elements.formBasicPassword.value;

    axios.post("https://europe-west1-smartbroeikas.cloudfunctions.net/api/login", {
      email,
      password
    }).then(result => {
      if (result.status === 200) {
        sessionStorage.setItem('login-token', result.data);
        console.log(result);
      } else {
        console.log(result);
      }
    }).catch(e => {
      if(e == 'Error: Request failed with status code 401'){
        alert('Email or password is incorrect');
      }

    });

  }

  render() {
      return (
        <div>
          <Container className="main-container">
            <Row>
              <Col xs={2}></Col>
              <Col xs={8}>      
                <div className="center-login-outer" /* style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh'}} */>
                  <div className="center-login-inner align-middle">
                    <h2>Login</h2>
                      <Form 
                          onSubmit={
                            (e) => this.postLogin(e)
                          }>
                        <Form.Group controlId="formBasicEmail">
                          <InputGroup>
                              <InputGroup.Prepend>
                                  <InputGroup.Text>
                                  <i className="fa fa-envelope"></i>
                                  </InputGroup.Text>
                              </InputGroup.Prepend>
                              <Form.Control
                                  type="email"
                                  placeholder="Mail"
                                  required
                              />
                          </InputGroup>
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                        <InputGroup>
                              <InputGroup.Prepend>
                                  <InputGroup.Text>
                                  <i className="fa fa-lock"></i>
                                  </InputGroup.Text>
                              </InputGroup.Prepend>
                              <Form.Control 
                                type="password"
                                placeholder="Password"
                                required
                              />
                          </InputGroup>                      
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox">
                          <Form.Check type="checkbox" label="Remember me" />
                        </Form.Group>
                        <Button variant="primary btn-login" type="submit">
                          Login
                        </Button>
                        <Form.Text className="text-muted text-center">
                            Not a member? <a href="#">Sign up</a> now!
                        </Form.Text>
                      </Form>
                  </div>
                </div>       
              </Col>
              <Col xs={2}></Col>
            </Row>
            <Footer></Footer>
          </Container>
        </div>
      );
    }
}

export default Login;
