import React, { Component } from 'react';
import axios from 'axios';

//Home Pagina
export class Dashboard extends Component {
    constructor(props){
        super(props);
        this.state = {
            logged_in: false
        };
    }

    componentDidMount = () => {       
        const header = 'Bearer ' + localStorage.getItem('jwt token')

        if( localStorage.getItem('jwt token') === null ){
            this.redirect();
        }

        axios.get('https://europe-west1-smartbroeikas.cloudfunctions.net/api/details', {headers: {Authorization:header}})
            .then(res => {
                this.setState({
                    user: res.data.credentials.email, 
                    hasPas: res.data.has_card, 
                    account:{
                        first_name:res.data.first_name,
                        surname: res.data.surname,
                        mail: res.data.mail,
                        dob: res.data.dob,
                        gender: res.data.gender
                    },
                    logged_in: true
                    })
                    console.log(res);
                    console.log(this.state.logged_in);
                    console.log(this.state);
                })            
            .catch((err) => {
               if(err === 'Error: Request failed with status code 403'){
                    this.redirect();
               }
            });
    }

    redirect = () => {
        localStorage.clear();
        window.location.href = "/login";
    }

    render() {
        return (
            <div>
                <h2>Dashboard</h2>
            </div>
        )
    }
}

export default Dashboard
