import React from 'react';
import './App.css';
import {Navbar,Nav} from 'react-bootstrap';
import SideNav, { Toggle, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import '@trendmicro/react-sidenav/dist/react-sidenav.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";
import Planten from './components/Planten';
import Users from './components/Users';
import Dashboard from './components/Dashboard';
import Login from './components/Login';
import { Form, Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";

class App extends React.Component{
  
  handleRoute = () => {
    return <Redirect to="/"/>
  }

  render (){
    if( window.location.pathname === '/login' ) {
      return (
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      );
  } else {
    return (
      <Router>
  
      <div className="App">
      <Nav style={{backgroundColor: '#49184F'}} className="justify-content-center">
      <Navbar  variant="dark" >
      <Navbar.Brand href="#home" style={{position:"center"}}>Smart Farm</Navbar.Brand>
      </Navbar>
      </Nav>
  
      <SideNav style={{backgroundColor:"#270F27"}}
            onSelect={(selected) => {
            }}
      >
  
      <SideNav.Nav defaultSelected="/">
          <NavItem eventKey="/">  
              <NavText>
                  <Link to="/">Dashboard</Link>
              </NavText>
          </NavItem>
          <NavItem eventKey="about">  
              <NavText>
              <Link to="/planten">Planten</Link>
              </NavText>
          </NavItem>
          <NavItem eventKey="users">  
              <NavText>
                  <Link to="/users">Users</Link> 
              </NavText>
          </NavItem>
          <NavItem eventKey="login">  
              <NavText>
                  <Link to="/login">Login</Link> 
              </NavText>
          </NavItem>     
      </SideNav.Nav>
      </SideNav>
      
      <Switch>
            <Route path="/planten">
              <Planten />
            </Route>
            <Route path="/users">
              <Users />
            </Route>
            <Route path="/login">
              <Login />
            </Route>
            <Route path="/">
              <Dashboard />
            </Route>
      </Switch>
  
      </div>
      </Router>
    );
  }
}
}

export default App;
