import React, { Component } from "react";
import { InputGroup, Form, Button, Row, Col, Container } from "react-bootstrap";
import axios from 'axios';
import 'font-awesome/css/font-awesome.min.css';
import '../login.css';
import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";

class Login extends Component {
  constructor(props) {
    super(props);
  }

  state = {
    user: {},
    email: "",
    password: "",
    account: {email: "", password: ""},
    loading: false,
    logged_in: false,
    login_status: "none",
    decodeToken: null,
    message:'',
 }

  handleSubmit = (event) => {
    event.preventDefault();

    const email = event.target.elements.formBasicEmail.value;
    const password = event.target.elements.formBasicPassword.value;

    this.setState({loading: true, logged_in: false, login_status: "none"})

    axios.post("https://europe-west1-smartbroeikas.cloudfunctions.net/api/login", {
      email,
      password
    }).then(result => {
      if (result.status === 200) {
          localStorage.setItem('jwt token', result.data);
        this.setState({ logged_in: true });
        this.props.history.push('/dashboard');
        window.location.reload();
      } else {
        console.log(result.status);
      }
    }).catch(e => {
      if(e === 'Error: Request failed with status code 401'){
        alert('Email or password is incorrect');
      }
    });
    console.log(this.state.logged_in);
    console.log('test');
  }

  render() {

      const override = css`
      display: block;
      margin: 0 auto;
      border-color: red;
    `;

      return (
        <div>
          <ClipLoader
            css={override}
            size={150}
            //size={"150px"} this also works
            color={"#123abc"}
            loading={this.state.loading}
          />
          <Container className="main-container">
            <Row>
              <Col xs={2}></Col>
              <Col xs={8}>      
                <div className="center-login-outer" /* style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh'}} */>
                  <div className="center-login-inner align-middle">
                    <h2>Login</h2>
                      <Form 
                          onSubmit={
                            (event) => this.handleSubmit(event)
                          }>
                        <Form.Group controlId="formBasicEmail">
                          <InputGroup>
                              <InputGroup.Prepend>
                                  <InputGroup.Text>
                                  <i className="fa fa-envelope"></i>
                                  </InputGroup.Text>
                              </InputGroup.Prepend>
                              <Form.Control
                                  type="email"
                                  placeholder="Mail"
                                  required
                              />
                          </InputGroup>
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                        <InputGroup>
                              <InputGroup.Prepend>
                                  <InputGroup.Text>
                                  <i className="fa fa-lock"></i>
                                  </InputGroup.Text>
                              </InputGroup.Prepend>
                              <Form.Control 
                                type="password"
                                placeholder="Password"
                                required
                              />
                          </InputGroup>                      
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox">
                          <Form.Check type="checkbox" label="Remember me" />
                        </Form.Group>
                        <Button userData = { this.state } onSubmit={this.handleSubmit} variant="primary btn-login" type="submit">
                          Login
                        </Button>
                        <Form.Text className="text-muted text-center">
                            Not a member? <a href="#">Sign up</a> now!
                        </Form.Text>
                      </Form>
                  </div>
                </div>       
              </Col>
              <Col xs={2}></Col>
            </Row>
            <Footer></Footer>
          </Container>
        </div>
      );
    }
}

function Footer({ children }) {
  return (
    <div>
      <div className="login-footer" style={{ backgroundImage: 'url(' + 'Login-trees.png' + ')' }}>{children}</div>
    </div>
  );
}

export default Login;
