import React, { Component, useState } from "react";
import { InputGroup, Form, Button, Row, Col, Container } from "react-bootstrap";
import 'font-awesome/css/font-awesome.min.css';
import { Redirect } from "react-router-dom";
import axios from 'axios';
import { useAuth } from "../context/auth";
import "../login.css";

let loginFooter = 'Login-trees.png'

const footerStyle = {
  backgroundImage: 'url(' + loginFooter + ')',
  fontSize: "20px",
  padding: "20px",
  position: "fixed",
  left: "0",
  bottom: "0",
  height: "200px",
  width: "100%"
};

function Footer({ children }) {
  return (
    <div>
      <div style={footerStyle}>{children}</div>
    </div>
  );
}

// function Login(props) {
  // const [isLoggedIn, setLoggedIn] = useState(false);
  // const [isError, setIsError] = useState(false);
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  // const { setAuthTokens } = useAuth();
  // const referer = props.location.state.referer || '/';

  function postLogin() {
    axios.post("https://europe-west1-smartbroeikas.cloudfunctions.net/api/login", {
      email,
      password
    }).then(result => {
      if (result.status === 200) {
        // setAuthTokens(result.data);
        setLoggedIn(true);
      } else {
        setIsError(true);
      }
    }).catch(e => {
      setIsError(true);
    });
    test();
  }

  if (isLoggedIn) {
    // return <Redirect to={referer} />;
  }

  function test() {
    console.log( password );
  }

  class Login extends Component {
  constructor(props){
    super(props);
    this.state={
    username:'',
    password:''
    }
  }
  render() {
      return (
        <div>
          <Container className="main-container">
            <Row>
              <Col xs={2}></Col>
              <Col xs={8}>
              
                <div className="center-login-outer" /* style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh'}} */>
                  <div className="center-login-inner align-middle">
                    <h2>Login</h2>
                      <Form>
                        <Form.Group controlId="formBasicEmail">
                          <InputGroup>
                              <InputGroup.Prepend>
                                  <InputGroup.Text>
                                  <i className="fa fa-envelope"></i>
                                  </InputGroup.Text>
                              </InputGroup.Prepend>
                              <Form.Control 
                              type="email" 
                              placeholder="Mail"
                              value={email}
                              onChange={e => {
                                setEmail(e.target.value);
                              }}
                              />
                          </InputGroup>
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                        <InputGroup>
                              <InputGroup.Prepend>
                                  <InputGroup.Text>
                                  <i className="fa fa-lock"></i>
                                  </InputGroup.Text>
                              </InputGroup.Prepend>
                              <Form.Control 
                                type="password"
                                value={password}
                                onChange={e => {
                                  setPassword(e.target.value);
                                }} 
                                placeholder="Password" />
                          </InputGroup>                      
                        </Form.Group>
                        <Form.Group controlId="formBasicCheckbox">
                          <Form.Check type="checkbox" label="Remember me" />
                        </Form.Group>
                        <Button onClick={postLogin} variant="primary btn-login" type="submit">
                          Login
                        </Button>
                        <Form.Text className="text-muted text-center">
                            Not a member? <a href="#">Sign up</a> now!
                        </Form.Text>
                        { isError && 'The username or password provided were incorrect!' }
                      </Form> 
                  </div>
                </div>       
              </Col>
              <Col xs={2}></Col>
            </Row>
            <Footer></Footer>
          </Container>
        </div>
      );
    }
  }
// }

export default Login;
