import React, { Component } from "react";
import { Form, Button, Row, Col, Container } from "react-bootstrap";
import "../login.css";

let loginFooter = 'Login-trees.png'

const footerStyle = {
  backgroundImage: 'url(' + loginFooter + ')',
  fontSize: "20px",
  padding: "20px",
  position: "fixed",
  left: "0",
  bottom: "0",
  height: "200px",
  width: "100%"
};

const phantomStyle = {
  display: "block",
  padding: "20px",
  height: "60px",
  width: "100%"
};

function Footer({ children }) {
  return (
    <div>
      <div style={phantomStyle} />
      <div style={footerStyle}>{children}</div>
    </div>
  );
}

class Login extends Component {
constructor(props){
  super(props);
  this.state={
  username:'',
  password:''
  }
 }
render() {
    return (
      <div>
        <Container>
          <Row>
            <Col xs={2}></Col>
            <Col xs={8}>
              <div className="center-login-outer">
                <div className="center-login-inner align-middle">
                  <h1>Login</h1>
                    <Form>
                      <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" placeholder="Mail" />
                      </Form.Group>
                      <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                      </Form.Group>
                      <Form.Group controlId="formBasicCheckbox">
                        <Form.Check type="checkbox" label="Save" />
                      </Form.Group>
                      <Form.Text className="text-muted text-center">
                          Not a member? <a href="#">Sign up</a> now!
                        </Form.Text>
                      <Button variant="primary btn-login" type="submit">
                        Login
                      </Button>
                    </Form> 
                </div>
              </div>       
            </Col>
            <Col xs={2}></Col>
          </Row>
          <Footer></Footer>
        </Container>
      </div>
    );
  }
}

export default Login;
