import React, { Component } from 'react'
import { Row, Card, Button, Container } from 'react-bootstrap';
import axios from 'axios';

//Planten Pagina
export class Planten extends Component {
    constructor(props) {
        super(props);

        this.state = {
            plants: []
        }
      }

    getPlants = async () => {
        try {
          return await axios.get('https://europe-west1-smartbroeikas.cloudfunctions.net/api/getplants')
        } catch (error) {
          console.error(error)
        }
    }

    logPlants = async () => {
        const plants = this.getPlants()
          .then(response => {
            if (response.data) {
                let listTest = [];
                response.data.map(function(item) {
                    listTest.push(item);
                })

                this.setState({
                    plants: listTest
                })
            }
          })
          .catch(error => {
            console.log(error)
          })
      }
      
      componentWillMount() {
        this.logPlants()
      }

    render() {
        console.log(this.state.plants);
        
        const plants = this.state.plants.map((item, index) => {
            return (
                <div>
                    <Row>
                        <Card style={{ width: '18rem' }}>
                            <img className="card-img-top" style={{ backgroundImage: 'url(' + 'placeholder-plant.jpg' + ')' }} />
                            <Card.Body>
                                <Card.Title>{item.body}</Card.Title>
                                <Card.Text>
                                {item.plantId}
                                </Card.Text>
                                <Button variant="primary">Check details</Button>
                            </Card.Body>
                        </Card> 
                    </Row>
                </div>
            )
        })

        return ( 
            <div>
                <h2>Planten</h2>


                <Container>
                {plants}
                <Row>
                    </Row>
                </Container>



            </div>
        )
    }
}

export default Planten
