import React, { Component } from 'react';
import axios from 'axios';
import { Planten } from './Planten';
import { UserDetails } from './UserDetails';
import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";

//Home Pagina
export class Dashboard extends Component {
    constructor(props){
        super(props);
        this.state = {
            logged_in: false,
            // loading: true
        };
    }

    componentDidMount = () => {
        this.state.loading = true;       
        const header = 'Bearer ' + localStorage.getItem('jwt token')

        if( localStorage.getItem('jwt token') === null ){
            this.redirect();
        }
        console.log(this.state.loading);

        axios.get('https://europe-west1-smartbroeikas.cloudfunctions.net/api/details', {headers: {Authorization:header}})
            .then(res => {
                this.setState({
                        test:      res.data.credentials.userId,
                        name:      res.data.credentials.email,
                        bio:       res.data.credentials.bio,
                        location:  res.data.credentials.location,
                        logged_in: true
                    })
                    console.log(res);
                    console.log(this.state.logged_in);
                    console.log(this.state);   
                })         
            .catch((err) => {
               if(err == 'Error: Request failed with status code 403'){
                    this.redirect();
               }
               console.log( err );
               this.setState(() => ({ loading: false }));
            });
    }

    redirect = () => {
        localStorage.clear();
        window.location.href = "/login";
    }

    render() {

        const override = css`
            display: block;
            margin: 0 auto;
            border-color: 4px solid #49184f;
            position: fixed;
            z-index: 999;
            height: 250px;
            width: 250px;
            overflow: visible;
            margin: auto;
            top: 0;
            left: 0;
            bottom: 0;
            right: 0;
        `;

        return (
            <div className="container">
                <ClipLoader
                    css={override}
                    size={250}
                    //size={"150px"} this also works
                    color={"#49184f"}
                    loading={this.state.loading}
                />
                <h2>Dashboard</h2>
                {this.state.logged_in}
                {/* <Planten userData = {this.state} /> */}
                <UserDetails userData = { this.state } />
            </div>
        )
    }
}

export default Dashboard
